INSERT INTO `projects` VALUES ('15', '2', '12', 'geolocation armenian map app', '', 'a vool app to know where you are and where to go', null, null, '', '', '', '398', '{\"id\":\"398##Yerevan##AM\",\"name\":\"Yerevan, AM\"}', 'AM', '', 'no_of_days', '25', null, '', '0', '1392163199', '300', '0', '0', null, '0', '0', '0', '0', '0', null, '0', '0', '1389993074', '0', null, '0', '0', '0', '1389993134');
INSERT INTO `projects` VALUES ('16', '12', '1', 'Portraits of Armenia', 'portraits-of-armenia', 'What defines a country? Its climate? Its history? Its traditions? Of course.\r\nBut what is it that really breathes life into a country? Where is its soul?\r\nIn its people. Those who unknowingly create an amazingly complex chain of thoughts, gestures, ways of being...And I\'d like to paint these people.', 'project_161390157335Portrait.jpg', null, '', '', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<blockquote>\r\n<h2 style=\"text-align: justify;\"><em>\"I found I could say things with color and shapes that I couldn\'t say any other way - things I had no words for\"&nbsp;</em></h2>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</blockquote>\r\n<p style=\"text-align: justify;\">My art has been exhibited in galleries throughout Europe. However, given my Armenian roots and that I\'m now living in Armenia, I would like to take this opportunity to paint.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2><strong>My portraits will be mirrors of this country and its people:</strong></h2>\r\n<blockquote>\r\n<h1 style=\"text-align: center;\"><img style=\"font-size: 11px; float: left;\" src=\"/img/stuff/4d36c41bf033b95b531b7e36188bd1a2.jpg\" alt=\"\" width=\"192\" height=\"155\" /></h1>\r\n</blockquote>\r\n<blockquote>\r\n<h1 style=\"text-align: justify;\"><strong>&nbsp;</strong></h1>\r\n</blockquote>\r\n<h2><strong>I intend to reveal Armenia\'s true colors to the world.&nbsp;</strong></h2>\r\n<h1 style=\"text-align: justify;\">&nbsp;</h1>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">I have decided to raise funds for my project via Boostbloom for several reasons:</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"padding-left: 60px;\">&nbsp;1. First of all because I\'d like to share with you the first steps of this project. The life of a painter is generally very lonely and crowdfunding seems the perfect opportunity to make it warmer. I would be sharing my daily work with you, specially those of you who would be willing to set off with me on this adventure.&nbsp;</p>\r\n<p style=\"padding-left: 60px;\">&nbsp;</p>\r\n<p style=\"padding-left: 60px;\">2. And also because given that my project would consist of painting daily images of Armenia/Armenians, I believe using an Armenian crowdfunding website is essential to make my work known in this country.&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">The money you would be granting me would be used ONLY for the material:</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<ul style=\"list-style-type: circle;\">\r\n<li style=\"text-align: left;\">2 sets of linen canvas for oil painting = 800$&nbsp;</li>\r\n<li style=\"text-align: left;\">12 wooden frames = 400$</li>\r\n<li style=\"text-align: left;\">Oil paint, varnish, brushes = 400$</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h3 style=\"text-align: center;\">&nbsp;Here is a list of my past exhibitions<span style=\"font-size: 11px;\">:</span></h3>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><strong>2008</strong> Centro Cultural Manuel de Falla&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Granada (Spain)</p>\r\n<p style=\"text-align: center;\"><strong>2009</strong> Galer&iacute;a Juana de Aizpuru</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Madrid (Spain)</p>\r\n<p style=\"text-align: center;\"><strong>2010</strong> Galerie Delafontaine</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Carouge (Switzerland)</p>\r\n<p style=\"text-align: center;\"><strong>2011</strong> Galerie Yellow Korner</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Casablanca (Morocco)</p>\r\n<p style=\"text-align: center;\"><strong>2012</strong> Galleria d\'arte Forma</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Milan (Italy)</p>\r\n<p style=\"text-align: center;\"><strong>2013</strong> Galeria Maxo</p>\r\n<p style=\"text-align: center;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Barcelona (Spain)</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">I leave you with a video of one of my favourite Armenian artists of today:</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\"><a href=\"/projects/edit/hamik/&lt;iframe width=&quot;420&quot; height=&quot;315&quot; src=&quot;/www.youtube.com/embed/BuNQhP3RpE4?rel=0&quot; frameborder=&quot;0&quot; allowfullscreen&gt;&lt;/iframe&gt;\"><iframe src=\"//www.youtube.com/embed/BuNQhP3RpE4?rel=0\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></a>&nbsp;</p>', '398', '{\"id\":\"398##Yerevan##AM\",\"name\":\"Yerevan, AM\"}', 'AM', '', 'no_of_days', '18', '1390156001', '', '0', '1397692799', '1600', '0', '0', null, '0', '0', '1', '1390157504', '1', null, '1', '0', '1390078284', '1390157399', null, '0', '0', '0', '1396132010');
INSERT INTO `projects` VALUES ('17', '13', '11', 'Interactive ebook for the children of today!', 'interactive-ebook-for-the-children-of-today', 'We are two school teachers, Arevik & Aline, and we like to write illustrated stories to inspire our youngest students. \r\nEveryone knows, that in order to hook children\'s attention, illustrations need to be eye-catching and bright, otherwise kids will get bored. Help us create INTERACTIVE EBOOKS!', 'project_1713905978391336578476_0.jpg', null, '', '', '<h2 style=\"text-align: center;\"><strong>The Dream Catcher,&nbsp;</strong><strong>The Timid Lion &amp; other stories</strong><span style=\"font-size: 11px;\">&nbsp;</span></h2>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-size: 11px;\">&nbsp;</span>Studies show that success in school and life starts with literacy:</h1>\r\n<h1 style=\"text-align: center;\">when children become good readers in their early years, they are more likely to become better learners throughout school and university.&nbsp;</h1>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h1 style=\"text-align: center;\">&middot;&middot;&middot;&middot;&middot;&middot;&nbsp;Who are we?&nbsp;&middot;&middot;&middot;&middot;&middot;&middot;</h1>\r\n<p style=\"text-align: center;\">&nbsp;We are a group of two: Aline is an IT professional and I (Arevik) am a school teacher.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h1 style=\"text-align: center;\">&middot;&middot;&middot;&middot;&middot;&middot; Our goal? &middot;&middot;&middot;&middot;&middot;&middot;&nbsp;</h1>\r\n<p style=\"text-align: center;\">Our goal is to make reading fun for children and to update the way in which they read stories. Nowadays, and as soon as children are capable of holding things, they begin to fiddle with anything within their reach, generalyy phones, tablets and other gadgets. Unlike us, they are children born in the era of technology.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">With your help, Aline and I aim to turn simple paper books into fun interactive ebooks. We believe children\'s story books also need to adapt to the new era.&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h1 style=\"text-align: center;\">&middot;&middot;&middot;&middot;&middot;&middot; Our project needs your help!!&nbsp;&middot;&middot;&middot;&middot;&middot;&middot;</h1>\r\n<p style=\"text-align: center;\">Recently, we contacted a publishing house for this project. We explained our idea and they agreed to publish our book!!!!! The only condition was that we would need to actually show them at least two interactive stories before.&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">This is why we have come to Boostbloom looking for your help. We believe this is the future of books for children. And we want to be pioneers. Join us and help us reach our goal. With the funds raised we will be able to turn our paper stories into beautiful e-imteractive stories.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h1 style=\"text-align: center;\">&middot;&middot;&middot;&middot;&middot;&middot; What\'s in it for you?&nbsp;&middot;&middot;&middot;&middot;&middot;&middot;</h1>\r\n<p style=\"text-align: center;\">&nbsp;Rewards will depend on your pledge: from having your name published on the book as one of the &nbsp;sponsors to receiving posters, three interactve e-stories or even a printed copy of the book.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h1 style=\"text-align: center;\">&middot;&middot;&middot;&middot;&middot;&middot;&nbsp;Thank you for taking the time to read our project &amp; we hope you decide to pledge&middot;&middot;&middot;&middot;&middot;&middot;</h1>\r\n<h1 style=\"text-align: center;\">&nbsp;</h1>\r\n<h1 style=\"text-align: center;\">Don\'t forget to check out this video about children explaining why they love to read&nbsp;</h1>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><iframe src=\"http://www.youtube.com/embed/xY_D6Ei7mO8\" width=\"425\" height=\"350\"></iframe>&nbsp;</p>', '398', '{\"id\":\"398##Yerevan##AM\",\"name\":\"Yerevan, AM\"}', 'AM', '', 'no_of_days', '22', '1390603958', '', '0', '1395964799', '1500', '0', '0', null, '0', '0', '1', '1390604080', '1', null, '1', '0', '1390243801', '1390604074', null, '1', '0', '0', '1395964803');
INSERT INTO `projects` VALUES ('23', '16', '15', 'Science bus around Armenia!!!', 'science-bus-around-armenia', 'Thanks to this Science Bus, children from all around Armenia will be able to take part in awesome experiments and learn Physics&Chemistry in a fun and dynamyc way. \r\nHelp us collect enough funds to get this magnificent project rolling! ALL ABOARD THE SCIENCE BUS!', 'project_231390777147science-bus.jpg', null, '', '', '<h2 style=\"text-align: center;\">&nbsp;ALL ABOARD!!!!!!!!</h2>\r\n<h2 style=\"text-align: center;\">&nbsp;About our project:</h2>\r\n<p style=\"text-align: justify;\">The Science Bus will be a mobile science laboratory that will bring science experiment workshops all around Armenia (specially to rural areas).</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">This Science Bus project has already had success in countries all around the world (Ireland, Italy, United States, etc). Scientist and educators have contributed to make the SCIENCE BUS a one-of-a-kind experience for children.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"font-size: 11px; float: left;\" src=\"/img/stuff/8f358af17867bc4748beada9b8eaa2ee.jpg\" alt=\"\" width=\"337\" height=\"266\" /></p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">Our program is aimed at children but also at teachers. We\'re looking to create a <span style=\"text-decoration: underline;\">domino effect</span>: if we show teachers how to easily make science fun for children, they will then be able to train other teachers.</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">At the end of the program we will provide teachers with the \"Book of experiments\" so they can repeat them in the future. And our students will be given the \"Try this at home\" book of experiments.</p>\r\n<h2 style=\"padding-left: 30px; text-align: left;\">&nbsp;</h2>\r\n<p style=\"text-align: justify;\">&nbsp;<span style=\"font-size: 11px;\">&nbsp;</span></p>\r\n<h2 style=\"text-align: center;\">&nbsp;We already have a bus!</h2>\r\n<h2 style=\"text-align: center;\">HELP US TURN IT INTO \"THE SCIENCE BUS\"!!</h2>\r\n<p style=\"text-align: justify;\">A very generous sponsor has already given us a bus. So our expenses have been considerably reduced.&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">However, what we need now is to adapt this ordinary bus and make it into a dazzling Science Bus. For this we will need: laboratory furniture, centrifuge, pipette, microscopes, safety goggles, fire extinguisher, gloves, petri dishes, flasks and beakers, alcohol burner, pipets, funnels, thermometer, tongs, gloves, chemical spoons, among other things.&nbsp;</p>\r\n<p style=\"text-align: justify;\"><br />Here is a video of only a few of the thousands of experiments our students will be carrying out aboard \"THE SCIENCE BUS\".</p>\r\n<p><iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.youtube.com/embed/ia8CKDIur3s\" width=\"211\" height=\"174\"></iframe></p>\r\n<h1 style=\"text-align: center;\">DON\'T HESITATE TO PLEDGE!</h1>\r\n<h1 style=\"text-align: center;\">TODAY YOU CAN HELP HUNDREDS OF ARMENIAN STUDENTS!</h1>\r\n<p>&nbsp;We leave you hoping you will pledge for this honorable cause. Thank you so much!</p>\r\n<p>&nbsp;</p>', '398', '{\"id\":\"398##Yerevan##AM\",\"name\":\"Yerevan, AM\"}', 'AM', '', 'no_of_days', '21', '1392476299', '', '0', '1395878399', '3500', '0', '0', null, '0', '0', '1', '1390825961', '1', null, '1', '0', '1390775624', '1390825940', null, '1', '0', '0', '1395878401');
